pragma solidity 0.8.26;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

import "./contexts/Base.sol";
import "../common/libs/Cmp.sol";
import "./Errors.sol";
import "./contexts/AddLiquidity.sol";
import "./Amm.sol";

uint256 constant MINIMUM_LIQUIDITY = 1_000;

abstract contract LiquidityPoolModule is AmmModule, ERC20 {
	function poolAddLiquidity(
		uint256 firstTokenOptimalAmount,
		uint256 secondTokenOptimalAmount,
		storagecache storage storageCache
	) internal returns (uint256 liquidity) {
		uint256 firstPotentialAmt = (firstTokenOptimalAmount *
			storageCache.lpTokenSupply) / storageCache.firstTokenReserve;
		uint256 secondPotentialAmt = (secondTokenOptimalAmount *
			storageCache.lpTokenSupply) / storageCache.secondTokenReserve;

		liquidity = Cmp.min(firstPotentialAmt, secondPotentialAmt);
		if (liquidity <= 0) {
			revert ErrorInsufficientLiquidity();
		}
		storageCache.lpTokenSupply += liquidity;

		storageCache.firstTokenReserve += firstTokenOptimalAmount;
		storageCache.secondTokenReserve += secondTokenOptimalAmount;
	}

	function poolAddInitialLiquidity(
		uint256 firstTokenOptimalAmount,
		uint256 secondTokenOptimalAmount,
		storagecache storage storageCache
	) internal returns (uint256 liquidity) {
		liquidity = Cmp.min(firstTokenOptimalAmount, secondTokenOptimalAmount);
		if (liquidity <= MINIMUM_LIQUIDITY) {
			revert ErrorFirstLiquidity();
		}

		// localMint -> Mint liquidity to contract
		_mint(address(this), MINIMUM_LIQUIDITY);

		storageCache.lpTokenSupply = liquidity;
		storageCache.firstTokenReserve += firstTokenOptimalAmount;
		storageCache.secondTokenReserve += secondTokenOptimalAmount;

		liquidity -= MINIMUM_LIQUIDITY;
	}

	function setOptimalAmounts(
		AddLiquidityContext memory context,
		storagecache storage storageCache
	) internal view returns (AddLiquidityContext memory) {
		uint256 firstTokenAmountDesired = context.firstPayment.amount;
		uint256 secondTokenAmountDesired = context.secondPayment.amount;

		bool isInitialLiqAdd = storageCache.lpTokenSupply == 0;
		if (isInitialLiqAdd) {
			context.firstTokenOptimalAmount = firstTokenAmountDesired;
			context.secondTokenOptimalAmount = secondTokenAmountDesired;

			return context;
		}

		uint256 secondTokenAmountOptimal = quote(
			firstTokenAmountDesired,
			storageCache.firstTokenReserve,
			storageCache.secondTokenReserve
		);

		if (secondTokenAmountOptimal <= secondTokenAmountDesired) {
			context.firstTokenOptimalAmount = firstTokenAmountDesired;
			context.secondTokenOptimalAmount = secondTokenAmountOptimal;
		} else {
			uint256 firstTokenAmountOptimal = quote(
				secondTokenAmountDesired,
				storageCache.secondTokenReserve,
				storageCache.firstTokenReserve
			);
			if (firstTokenAmountOptimal > firstTokenAmountDesired) {
				revert ErrorOptimalGreaterThanPaid();
			}
			context.firstTokenOptimalAmount = firstTokenAmountOptimal;
			context.secondTokenOptimalAmount = secondTokenAmountDesired;
		}
		if (context.firstTokenOptimalAmount < context.firstTokenAmountMin) {
			revert ErrorInsufficientFirstToken();
		}
		if (context.secondTokenOptimalAmount < context.secondTokenAmountMin) {
			revert ErrorInsufficientSecondToken();
		}

		return context;
	}
}
","keccak256":"0x049d8d35beb4d0c32b2400a3ddc7f2d3f37f0aa9b124e51e769dc14ac165eb9b","license":"MIT"},"contracts/pair/Pair.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

import "../common/modules/PausableModule.sol";
import "../common/modules/BannedAddressModule.sol";

import "./ConfigModule.sol";

import "./pair_actions/AddLiquidityModule.sol";

contract Pair is
	ConfigModule,
	PausableModule,
	BannedAdddressModule,
	AddLiquidityModule
{
	error InvalidTokenAddress();
	error ErrorSameTokens();

	constructor(
		address firstToken_,
		address secondToken_,
		address routerAddress_,
		address routerOwnerAddress,
		uint256 totalFeePercent_,
		uint256 specialFeePercent_,
		address initialLiquidityAdder_
	) ERC20("Pair-LP", "PLP") {
		if (firstToken_ == address(0)) revert InvalidTokenAddress();
		if (secondToken_ == address(0)) revert InvalidTokenAddress();
		if (firstToken_ == secondToken_) revert ErrorSameTokens();

		_setFeePercents(totalFeePercent_, specialFeePercent_);
		state = State.Inactive;
		routerAddress = routerAddress_;

		firstToken = firstToken_;
		secondToken = secondToken_;
		lpTokenIdentifier = address(this);

		initialLiquidityAdder = initialLiquidityAdder_;

		_setupRole(DEFAULT_ADMIN_ROLE, msg.sender);

		bytes32[] memory allPermissions = new bytes32[](3);
		allPermissions[0] = PermissionOWNER;
		allPermissions[1] = PermissionADMIN;
		allPermissions[2] = PermissionPAUSE;

		addRoles(allPermissions, routerAddress);
		addRoles(allPermissions, routerOwnerAddress);

		addBannedAddress(address(this));
	}
}
","keccak256":"0xbac281fa662c69ff34fbde67cd8b8be54307c83bd35b9b02fa81cf177bf4a0bb","license":"MIT"},"contracts/pair/SafePrice.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

import "./Errors.sol";

// TODO use when we can do operator overloading; see https://docs.soliditylang.org/en/v0.8.23/contracts.html#using-for
// type Round is uint256;

uint constant MAX_OBSERVATIONS = 65_536; // 2^{16} records, to optimise binary search

struct PriceObservation {
	uint256 firstTokenReserveAccumulated;
	uint256 secondTokenReserveAccumulated;
	uint256 weightAccumulated;
	// Round recordingRound;
	uint256 recordingRound;
}

struct IndexValue {
	uint keyIndex;
	PriceObservation value;
}

struct priceObsvec {
	mapping(uint => IndexValue) data;
	uint[] keys;
	uint length;
}

library PriceObsVec {
	function isEmpty(
		priceObsvec storage self
	) internal view returns (bool empty) {
		empty = self.length == 0;
	}

	function get(
		priceObsvec storage self,
		uint index
	) internal view returns (PriceObservation memory) {
		if (index > self.length) {
			revert IndexOutOfRangeErrMsg();
		}

		return self.data[index].value;
	}

	function set(
		priceObsvec storage self,
		uint index,
		PriceObservation memory value
	) internal {
		if (index > self.length) {
			revert IndexOutOfRangeErrMsg();
		}

		self.data[index].value = value;
		self.data[index].keyIndex = index;
	}

	/// Add one item at the end of the list.
	/// Returns the index of the newly inserted item, which is also equal to the new number of elements.
	function push(
		priceObsvec storage self,
		PriceObservation memory value
	) internal returns (uint) {
		self.length += 1;

		self.data[self.length].value = value;
		self.data[self.length].keyIndex = self.length;

		return self.length;
	}

	function len(priceObsvec storage self) internal view returns (uint) {
		return self.length;
	}
}

abstract contract SafePriceModule {
	uint safePriceCurrentIndex;

	priceObsvec priceObservations;
	using PriceObsVec for priceObsvec;

	function updateSafePrice(
		uint256 firstTokenReserve,
		uint256 secondTokenReserve
	) internal {
		if (firstTokenReserve == 0 || secondTokenReserve == 0) {
			return;
		}

		uint currentRound = block.number;

		if (safePriceCurrentIndex > MAX_OBSERVATIONS) {
			revert ErrorSafePriceCurrentIndex(
				safePriceCurrentIndex,
				MAX_OBSERVATIONS
			);
		}

		PriceObservation memory lastPriceObservation;
		uint newIndex = 1;
		if (!priceObservations.isEmpty()) {
			lastPriceObservation = priceObservations.get(safePriceCurrentIndex);
			newIndex = (safePriceCurrentIndex % MAX_OBSERVATIONS) + 1;
		}

		if (lastPriceObservation.recordingRound == currentRound) {
			return;
		}

		PriceObservation memory newPriceObservation = computeNewObservation(
			currentRound,
			firstTokenReserve,
			secondTokenReserve,
			lastPriceObservation
		);

		if (priceObservations.len() == MAX_OBSERVATIONS) {
			priceObservations.set(newIndex, newPriceObservation);
		} else {
			priceObservations.push(newPriceObservation);
		}

		safePriceCurrentIndex = newIndex;
	}

	function computeNewObservation(
		uint newRound,
		uint256 newFirstReserve,
		uint256 newSecondReserve,
		PriceObservation memory currentPriceObservation
	) internal pure returns (PriceObservation memory newPriceObservation) {
		uint256 newWeight = currentPriceObservation.recordingRound == 0
			? 1
			: newRound - currentPriceObservation.recordingRound;

		newPriceObservation = currentPriceObservation;
		newPriceObservation.firstTokenReserveAccumulated +=
			newWeight *
			newFirstReserve;
		newPriceObservation.secondTokenReserveAccumulated +=
			newWeight *
			newSecondReserve;
		newPriceObservation.weightAccumulated += newWeight;
		newPriceObservation.recordingRound = newRound;
	}
}
