pragma solidity 0.8.20;

import "@openzeppelin/contracts/interfaces/IERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import { SFT } from "../../modules/SFT.sol";
import { WEDU } from "./WEDU.sol";

struct TokenPayment {
	address token;
	uint256 amount;
	uint256 nonce;
}

library TokenPayments {
	using Address for address;

	function receiveToken(TokenPayment memory payment) internal {
		receiveToken(payment, msg.sender);
	}

	function receiveToken(TokenPayment memory payment, address from) internal {
		if (msg.value > 0) {
			// Native payment (ETH)
			require(
				payment.amount == msg.value,
				"TokenPayments: ETH amount mismatch"
			);
			require(
				from == msg.sender,
				"TokenPayments: Native payment must be from caller"
			);

			// Wrap EDU into WEDU
			WEDU(payable(payment.token)).deposit{ value: msg.value }();
		} else if (payment.nonce == 0) {
			// ERC20 payment
			IERC20(payment.token).transferFrom(
				from,
				address(this),
				payment.amount
			);
		} else {
			// SFT payment
			SFT(payment.token).safeTransferFrom(
				from,
				address(this),
				payment.nonce,
				payment.amount,
				""
			);
		}
	}

	event Token(address indexed token, bool indexed moved);

	function sendToken(TokenPayment memory payment, address to) internal {
		if (payment.nonce == 0) {
			uint256 beforeNativeBal = address(this).balance;

			// Try to withdraw ETH assuming payment.token is WEDU
			(bool shouldMoveEthBalance, ) = payment.token.call(
				abi.encodeWithSignature("withdraw(uint256)", payment.amount)
			);

			emit Token(payment.token, shouldMoveEthBalance);

			// Checks to ensure balance movements
			if (shouldMoveEthBalance) {
				require(
					(beforeNativeBal + payment.amount) == address(this).balance,
					"Failed to withdraw WEDU"
				);
			}

			if (shouldMoveEthBalance) {
				payable(to).transfer(payment.amount);
			} else {
				IERC20(payment.token).transfer(to, payment.amount);
			}
		} else {
			// SFT payment
			SFT(payment.token).safeTransferFrom(
				address(this),
				to,
				payment.nonce,
				payment.amount,
				""
			);
		}
	}

	function approve(TokenPayment memory payment, address to) internal {
		if (payment.nonce == 0) {
			// ERC20 approval
			IERC20(payment.token).approve(to, payment.amount);
		} else {
			// SFT approval
			SFT(payment.token).setApprovalForAll(to, true);
		}
	}
}
","keccak256":"0x44c7194ae0bef0f1aadc70d0252c8b750446168b449bf657e4249e660523a1cf","license":"MIT"},"contracts/common/libs/WEDU.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import { ERC20Upgradeable } from "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";

/// @title WEDU (Wrapped EduChain) Token
/// @notice This contract wraps EduChain (EDU) native tokens into ERC20-compliant WEDU tokens.
contract WEDU is ERC20Upgradeable {
	/// @notice Constructor initializes the ERC20Upgradeable token with name and symbol.
	function initialize() public initializer {
		__ERC20_init("Wrapped EduChain", "WEDU");
	}

	/// @notice Fallback function to receive EDU and automatically wrap it into WEDU.
	/// The received EDU will be wrapped as WEDU and credited to the sender.
	receive() external payable {
		deposit();
	}

	/// @notice Wraps EDU into WEDU tokens. The amount of WEDU minted equals the amount of EDU sent.
	/// @dev This function mints WEDU tokens equivalent to the amount of EDU sent by the user.
	function deposit() public payable {
		_mint(msg.sender, msg.value);
	}

	/// @notice Unwraps WEDU tokens back into EDU.
	/// @param amount The amount of WEDU tokens to unwrap.
	/// @dev This function burns the specified amount of WEDU tokens and sends the equivalent amount of EDU to the user.
	function withdraw(uint256 amount) public {
		require(balanceOf(msg.sender) >= amount, "WEDU: Insufficient balance");
		_burn(msg.sender, amount);
		payable(msg.sender).transfer(amount);
	}

	/// @notice Allows an approved spender to use WEDU tokens on behalf of the sender.
	/// @param owner The address of the token owner.
	/// @param spender The address of the spender allowed to use the tokens.
	/// @dev This function mints WEDU tokens to the owner and approves the spender to use the minted tokens.
	function receiveForSpender(address owner, address spender) public payable {
		_mint(owner, msg.value);
		_approve(owner, spender, msg.value);
	}
}
