// SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

import { SFT } from "./SFT.sol";

contract LpToken is SFT {
	struct LpAttributes {
		uint256 rewardPerShare;
		uint256 depValuePerShare;
		address pair;
		address tradeToken;
	}

	struct LpBalance {
		uint256 nonce;
		uint256 amount;
		LpAttributes attributes;
	}

	constructor() SFT("Academy-DEX LP Token", "LPADEX") {}

	/// @notice Returns the SFT balance of a user including detailed attributes.
	/// @param user The address of the user to check.
	/// @return An array of `LpBalance` containing the user's balance details.
	function lpBalanceOf(
		address user
	) public view returns (LpBalance[] memory) {
		SftBalance[] memory _sftBals = _sftBalance(user);
		LpBalance[] memory balance = new LpBalance[](_sftBals.length);

		for (uint256 i; i < _sftBals.length; i++) {
			SftBalance memory _sftBal = _sftBals[i];

			balance[i] = LpBalance({
				nonce: _sftBal.nonce,
				amount: _sftBal.amount,
				attributes: abi.decode(_sftBal.attributes, (LpAttributes))
			});
		}

		return balance;
	}

	function mint(
		uint256 rewardPerShare,
		uint256 lpAmount,
		address pair,
		address tradeToken,
		address to,
		uint256 depValuePerShare
	) external onlyOwner returns (uint256) {
		require(lpAmount > 0, "LpToken: LP Amount must be greater than 0");

		bytes memory attributes = abi.encode(
			LpAttributes({
				rewardPerShare: rewardPerShare,
				pair: pair,
				tradeToken: tradeToken,
				depValuePerShare: depValuePerShare
			})
		);

		return _mint(to, lpAmount, attributes);
	}

	function getBalanceAt(
		address user,
		uint256 nonce
	) public view returns (LpBalance memory) {
		require(hasSFT(user, nonce), "No Lp balance found at nonce for user");

		return
			LpBalance({
				nonce: nonce,
				amount: balanceOf(user, nonce),
				attributes: abi.decode(
					_getRawTokenAttributes(nonce),
					(LpAttributes)
				)
			});
	}

	function split(
		uint256 nonce,
		address[] calldata addresses,
		uint256[] calldata portions
	) external returns (uint256[] memory splitNonces) {
		require(addresses.length > 1, "LpToken: addresses too short");
		require(
			addresses.length == portions.length,
			"LpToken: Portions addresses missmatch"
		);

		address caller = msg.sender;
		LpBalance memory lpBalance = getBalanceAt(caller, nonce);

		_burn(caller, nonce, lpBalance.amount);
		uint256 totalSplitAmount = 0;
		splitNonces = new uint256[](addresses.length);

		bytes memory attributes = abi.encode(lpBalance.attributes);
		for (uint256 i; i < addresses.length; i++) {
			address to = addresses[i];
			uint256 amount = portions[i];
			totalSplitAmount += amount;

			splitNonces[i] = _mint(to, amount, attributes);
		}
		require(
			totalSplitAmount == lpBalance.amount,
			"LpToken: Invalid Portions"
		);
	}
}

contracts/modules/SFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract SFT is ERC1155, Ownable {
	using Counters for Counters.Counter;
	using EnumerableSet for EnumerableSet.UintSet;

	struct SftBalance {
		uint256 nonce;
		uint256 amount;
		bytes attributes;
	}

	Counters.Counter private _nonceCounter;
	string private _name;
	string private _symbol;

	// Mapping from nonce to token attributes as bytes
	mapping(uint256 => bytes) private _tokenAttributes;

	// Mapping from address to list of owned token nonces
	mapping(address => EnumerableSet.UintSet) private _addressToNonces;

	constructor(string memory name_, string memory symbol_) ERC1155("") {
		_name = name_;
		_symbol = symbol_;
	}

	/// @dev Internal function to mint new tokens with attributes and store the nonce.
	function _mint(
		address to,
		uint256 amount,
		bytes memory attributes
	) internal returns (uint256 nonce) {
		_nonceCounter.increment();
		nonce = _nonceCounter.current();

		// Store the attributes
		_tokenAttributes[nonce] = attributes;

		// Mint the token with the nonce as its ID
		super._mint(to, nonce, amount, "");

		// Track the nonce for the address
		_addressToNonces[to].add(nonce);
	}

	/// @dev Returns the name of the token.
	function name() public view returns (string memory) {
		return _name;
	}

	/// @dev Returns the symbol of the token.
	function symbol() public view returns (string memory) {
		return _symbol;
	}

	/// @dev Returns the token name and symbol.
	function tokenInfo() public view returns (string memory, string memory) {
		return (_name, _symbol);
	}

	/// @dev Returns raw token attributes by nonce.
	/// @param nonce The nonce of the token.
	/// @return Attributes in bytes.
	function _getRawTokenAttributes(
		uint256 nonce
	) internal view returns (bytes memory) {
		return _tokenAttributes[nonce];
	}

	/// @dev Returns the list of nonces owned by an address.
	/// @param owner The address of the token owner.
	/// @return Array of nonces.
	function getNonces(address owner) public view returns (uint256[] memory) {
		return _addressToNonces[owner].values();
	}

	/// @dev Checks if the address owns a specific nonce.
	/// @param owner The address of the token owner.
	/// @param nonce The nonce to check.
	/// @return True if the address owns the nonce, otherwise false.
	function hasSFT(address owner, uint256 nonce) public view returns (bool) {
		return _addressToNonces[owner].contains(nonce);
	}

	/// @dev Burns the tokens of a specific nonce and mints new tokens with updated attributes.
	/// @param user The address of the token holder.
	/// @param nonce The nonce of the token to update.
	/// @param amount The amount of tokens to mint.
	/// @param attr The new attributes to assign.
	/// @return The new nonce for the minted tokens.
	function update(
		address user,
		uint256 nonce,
		uint256 amount,
		bytes memory attr
	) public onlyOwner returns (uint256) {
		_burn(user, nonce, amount);
		return amount > 0 ? _mint(user, amount, attr) : 0;
	}

	/// @dev Returns the balance of the user with their token attributes.
	/// @param user The address of the user.
	/// @return Array of SftBalance containing nonce, amount, and attributes.
	function _sftBalance(
		address user
	) internal view returns (SftBalance[] memory) {
		uint256[] memory nonces = getNonces(user);
		SftBalance[] memory balance = new SftBalance[](nonces.length);

		for (uint256 i; i < nonces.length; i++) {
			uint256 nonce = nonces[i];
			bytes memory attributes = _tokenAttributes[nonce];
			uint256 amount = balanceOf(user, nonce);

			balance[i] = SftBalance({
				nonce: nonce,
				amount: amount,
				attributes: attributes
			});
		}

		return balance;
	}

	/// @dev Override _beforeTokenTransfer to handle address-to-nonce mapping.
	/// @param operator The address performing the transfer.
	/// @param from The address sending tokens.
	/// @param to The address receiving tokens.
	/// @param ids The token IDs being transferred.
	/// @param amounts The amounts of tokens being transferred.
	/// @param data Additional data.
	function _beforeTokenTransfer(
		address operator,
		address from,
		address to,
		uint256[] memory ids,
		uint256[] memory amounts,
		bytes memory data
	) internal virtual override {
		super._beforeTokenTransfer(operator, from, to, ids, amounts, data);

		for (uint256 i = 0; i < ids.length; i++) {
			uint256 id = ids[i];

			_addressToNonces[from].remove(id);
			_addressToNonces[to].add(id);
		}
	}
}

